    "type": "contentBlock",
    "pageName": "Communication Styles",
    "category": "Communication / Communication Styles",
    "url": "../html/communication-styles-fr8Fx6Cr#search-42ffdf9b-2629-442c-a048-1801c722ec4a-1-1-4"
