    "type": "contentBlock",
    "pageName": "Communication Styles",
    "category": "Communication / Communication Styles",
    "url": "../html/communication-styles-fr8Fx6Cr#search-eca1e9e1-ccae-4a83-914d-8d33fc2d4ac4-1-1-6"
